import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      items:{},
      moreItem:{
        text:'',
        key:'',
      }
    }
    this.handleInput = this.handleInput.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  handleInput(e) {
    this.setState({
      moreItem:{
        text: e.target.value,
        key:Date.now()
      }
    })
  }

  addItem(e) {
    e.preventDefault();
    const newItem = this.state.moreItem;
    console.log(newItem);
  }
  render(){
    return (
      <div className="Todo">
        <header>
        <from id='form' onSubmit={this.addItem}>
          <input type="text" placeholder="Enter Text"/>
          value={this.state.moreItem.text}
          onChange={this.handleInput}/>
          <button type="submit">Add</button>
        </from>
      </header>
      </div>
    );
  }
}

export default App;
